package com.hjh.chapter03;import java.io.*;import java.net.Socket;import java.util.Scanner;public class CreateAndConnectionClient {    public static void main(String[] args) {        try (Scanner sc = new Scanner(System.in);             Socket clientSocket = new Socket("127.0.0.1", 9999)        ) {            var in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));            var out = new BufferedWriter(new OutputStreamWriter(clientSocket.getOutputStream()));            while (true) {                System.out.print("글자를 입력해주세요!!");                String clientMsg = sc.nextLine();                out.write(clientMsg + "\n");                out.flush();                if (clientMsg.equals("exit")) {                    System.out.println("서버와의 1 연결이 종료되었습니다.");                    break;                }                String serverMsg = in.readLine();                System.out.println("서버로부터 온 메세지: " + serverMsg + "\n");                if (serverMsg == null || serverMsg.equals("exit")) {                    System.out.println("서버와의 2 연결이 종료되었습니다.");                    break;                }            }        } catch (IOException e) {            System.out.println("여기오나?");            e.printStackTrace();        }    }}